import React, {Component} from "react"

class Main extends Component {

    constructor(props) {
        super(props)
        this.state = {
          gender: 'male',
          height: 0,
          weight: 0,
          age: 0,
          showIncompleteWarning: false
        }
        this.onDetailChange = this.onDetailChange.bind(this)
      }

      onDetailChange(detail, value) {
        let newState = this.state
        newState[detail] = (detail === 'gender' ? value : (value === '' ? 0 : parseInt(value, 10)))
        this.setState(newState)
      }
    
      onCalculate() {
        let { gender, age, height, weight } = this.state,
          bmr = 0
        if (age && height && weight) {
          if (gender === 'male') {
            bmr = 10 * weight + 6.25 * height - 5 * age + 5
          } else {
            bmr = 10 * weight + 6.25 * height - 5 * age - 161
          }
          this.props.actions.initializeBmr( bmr )
        } else {
          this.setState({
            showIncompleteWarning: true
          })
        }
      }
    
      handleRequestClose = () => {
        this.setState({
          showIncompleteWarning: false
        });
      }

    render(){
    return (
    <div>
        <div className="block-cards">
                    <div className="item-card">
                      <form>
                        <input
                        label="Рост"
                        onChange={(e, value) => { this.onDetailChange('height', value) }} />

                      </form>
                    </div>
            </div>
    </div>
    )
    }
}

export default Main
