import React, { Component } from "react";

class Menu extends Component {

    state = {
        page: ''
    }    

    activePage() {
        this.setState(state => ({
          isToggleOn: !state.isToggleOn
        }));
      }

    render(){        
    return(
            <div className="menu-block">
                    <div className="menu-item"
                    page="main"
                    onClick={this.activePage}>
                        Главная
                    </div>
                    <div className="menu-item"
                    page="about"
                    onClick={this.activePage}>
                        О продуктах
                    </div>
                    <div className="menu-item"
                    onClick={this.activePage}>
                        Выйти
                    </div>
            </div>
    )
}
}

export default Menu
