import React,{ useState, useEffect } from "react"

import { ProductsBlock } from './index'

function ProductsBlock_v2({title, data, search}) {

    const [isOpen, setIsOpen] = useState(false);
    useEffect(() => {
      if(search.lenght)setIsOpen(true)
    }, [search])

  return (
    <div>
        <h2 onClick = {() => setIsOpen(!isOpen)}>{title}</h2>
        {
          isOpen && 
          data.filter(product=>product.name.toLowerCase().includes(search.toLowerCase())).map((product) => (<ProductsBlock 
          key = {product.id}
          {...product}/>))
        }
    </div>
  )
}

export default ProductsBlock_v2
