import React,{ useState, useEffect } from "react"

import { ProductsBlock } from './index'

function ProductsBlock_v2({title, data, search, imgsrc}) {

    const [isOpen, setIsOpen] = useState(false);
    useEffect(() => {
       if(search.length)setIsOpen(true)
    }, [search])

  return (
    <div className="category-block"  onClick = {() => setIsOpen(!isOpen)}>
        <img src={imgsrc}/>
        <h2>{title}</h2>
        {
          isOpen && 
          data.filter(product=>product.name.toLowerCase().includes(search.toLowerCase())).map((product) => (<ProductsBlock 
          key = {product.id}
          {...product}/>))
        }
    </div>
  )
}

export default ProductsBlock_v2
