import React, { Component } from "react";

class Menu extends Component { 

    constructor() {
        super();
        this.state = {
            page: 'main'
        };
        this.handleActivePage = this.handleActivePage.bind(this);
    }

    handleActivePage() {
        this.setState({
            page: 1
        });
        console.log(this.state.page)
    }

    render(){          
    return(
            <div className="menu-block">
                    <div className="menu-item"
                    page="main"
                    onClick={this.handleActivePage}>
                        Главная
                    </div>
                    {this.state.page}
                    <div className="menu-item"
                    page="about"
                    onClick={this.handleActivePage}>
                        О продуктах
                    </div>
                    <div className="menu-item"
                    onClick={this.handleActivePage}>
                        Выйти
                    </div>
            </div>
    )
}
}

export default Menu
